SET NAMES utf8mb4;
CREATE DATABASE IF NOT EXISTS `ma_base_mysql` CHARACTER SET utf8mb4;
USE `ma_base_mysql`;

CREATE TABLE IF NOT EXISTS `admins` (
  `doc_path`   VARCHAR(2048) PRIMARY KEY,
  `id`         VARCHAR(512) NOT NULL,
  `parent_path` VARCHAR(1536) NOT NULL,
  `data`       JSON NOT NULL
) CHARACTER SET utf8mb4;

CREATE TABLE IF NOT EXISTS `ideas` (
  `doc_path`   VARCHAR(2048) PRIMARY KEY,
  `id`         VARCHAR(512) NOT NULL,
  `parent_path` VARCHAR(1536) NOT NULL,
  `data`       JSON NOT NULL
) CHARACTER SET utf8mb4;

CREATE TABLE IF NOT EXISTS `inscriptions` (
  `doc_path`   VARCHAR(2048) PRIMARY KEY,
  `id`         VARCHAR(512) NOT NULL,
  `parent_path` VARCHAR(1536) NOT NULL,
  `data`       JSON NOT NULL
) CHARACTER SET utf8mb4;

CREATE TABLE IF NOT EXISTS `votes` (
  `doc_path`   VARCHAR(2048) PRIMARY KEY,
  `id`         VARCHAR(512) NOT NULL,
  `parent_path` VARCHAR(1536) NOT NULL,
  `data`       JSON NOT NULL
) CHARACTER SET utf8mb4;

